import styled from 'styled-components';


export const Caixa = styled.h1`
color: #f2f;
font-size:${props => `${props.fonte}px`};

background: ${props.wallpaper}

h2{
    color: #F00;
    font-size: 15px;
}
`;

export const Texto = styled.h1`

`;
