import React from 'react';
import { Caixa, Texto } from '../../styles/styles';
import Fundo from '../../images/fundo.jpg'
import Sorteio from '../../Backend/GeradorNumeros';

export default props =>{

    //Logica para o sorteio
    const NumeroAlatorio = _ =>{
        let numero = Math.floor(Math.random() * (60 - 1)+1);
        return numero;
    };
    const Sorteio = quantidade =>{
        let sorteados = [];

        for(let i = 1; i <= quantidade; i++){
            let numero = NumeroAlatorio();
            sorteados.push(numero);
        }
        return sorteados;
    } 
    Sorteio(5)


    return(
        <Caixa fonte={20}> 
            <h1 cor={"#222"} fonte={0}>Sorteador</h1>
            <h2 fonte={80}>OUTRO TEXTO</h2>
        </Caixa>
    );
}
